mapper.select:
select d.gender as gender,nvl(sum(d.imei_num),0) as gnum from B_REGION d where 1=1 and d.area_type in ('Indoor','Outdoor')
and d.stadium_no=#{region}
and d.sdate=#{sdate} group by d.gender

select d.age as age,nvl(sum(d.imei_num),0) as anum from B_REGION d where 1=1 and d.area_type in ('Indoor','Outdoor')
and d.stadium_no=#{region}
and d.sdate=#{sdate} group by d.age

select * from (
select d.source as source,nvl(sum(d.imei_num),0) as snum
from B_REGION d 
where 1=1
and d.area_type in ('Indoor','Outdoor')  

	and d.stadium_no=#{region} 

and d.sdate=#{sdate} 
and d.sourcetype=#{sourcetype}
group by d.source  
order by nvl(sum(d.imei_num),0) desc
)
where ROWNUM <=10
